import React from 'react';
import { useNavigate } from 'react-router-dom';
import './RoleCards.css';

function RoleCards() {
  const navigate = useNavigate();

  const roles = [
    {
      title: 'Consumer',
      description: 'Browse products, place orders, and track deliveries in real-time with ease.',
      image: 'https://www.shutterstock.com/image-photo/shopping-young-smiling-woman-holding-600nw-2493027649.jpg',
    },
    {
      title: 'Marketplace Manager',
      description: 'Manage product listings, inventory, pricing, and optimize customer experience.',
      image: 'https://www.shutterstock.com/image-photo/young-couple-supermarket-doing-daily-260nw-1620167797.jpg',
    },
    {
      title: 'Warehouse Manager',
      description: 'Oversee stock, ensure order accuracy, and streamline supply chain operations.',
      image: 'data:image/jpeg;base64,',
    },
  ];

  const handleClick = (role) => {
    navigate('/login', { state: { selectedRole: role } });
  };

  return (
    <div className="role-cards-section">
      <div className="role-cards-container">
        <div className="role-cards-header">
          <h2>Choose Your Role !!</h2>
          <p>Select your role to access the platform's features</p>
        </div>
        
        <div className="role-cards-grid">
          {roles.map((role, index) => (
            <div className="role-card" key={index}>
              <div className="card-image-container">
                <img src={role.image} alt={`${role.title}`} />
              </div>
              <div className="card-content">
                <h3>{role.title}</h3>
                <p>{role.description}</p>
                <button 
                  className="role-card-button"
                  onClick={() => handleClick(role.title)}
                >
                  Login as {role.title}
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default RoleCards;